import React, { Component } from "react";
import moment from "moment";
import api from "../../services/api";

import logo from "../../assets/logo.png";

import { Container, Form } from "./styles";

import CompareList from "../../components/CompareList";

class Main extends Component {
  state = {
    loading: false,
    repositoryError: false,
    repositoryInput: "",
    repositories: []
  };

  // componentDidMount() {
  //   localStorage.removeItem("repositories");
  // }

  componentDidMount() {
    let repositories = JSON.parse(localStorage.getItem("repositories")) || [];
    this.setState({ repositories });
    console.log(repositories);
  }

  handleAddRepository = async e => {
    e.preventDefault();

    this.setState({ loading: true });

    try {
      const { data: repository } = await api.get(
        `/repos/${this.state.repositoryInput}`
      );

      repository.lastCommit = moment(repository.pushed_at).fromNow();

      this.setState(
        {
          repositoryError: false,
          repositoryInput: "",
          repositories: [...this.state.repositories, repository]
        },
        () => {
          localStorage.setItem(
            "repositories",
            JSON.stringify(this.state.repositories)
          );
        }
      );
    } catch (err) {
      this.setState({
        repositoryError: true
      });
    } finally {
      this.setState({ loading: false });
    }
  };

  handleRefreshRepo = async (e, { id, owner, name }) => {
    e.preventDefault();
    try {
      const { data: newRepository } = await api.get(`/repos/${owner}/${name}`);

      newRepository.lastCommit = moment(newRepository.pushed_at).fromNow();

      const repositories = this.state.repositories.map(repository => {
        if (repository.id === id) {
          repository = newRepository;
        }
      });

      this.setState(
        {
          repositories
        },
        () => {
          localStorage.setItem(
            "repositories",
            JSON.stringify(this.state.repositories)
          );
        }
      );
    } catch (err) {
      alert("Error updating repository");
    } finally {
      // this.setState({ loading: false });
    }
  };

  handleDeleteRepo = id => {
    const repositories = this.state.repositories.filter(
      repository => repository.id !== id
    );
    this.setState({ repositories }, () => {
      localStorage.setItem(
        "repositories",
        JSON.stringify(this.state.repositories)
      );
    });
  };

  render() {
    return (
      <Container>
        <img src={logo} alt="Github Compare" />

        <Form
          withError={this.state.repositoryError}
          onSubmit={this.handleAddRepository}
        >
          <input
            type="text"
            placeholder="usuário/repositório"
            value={this.state.repositoryInput}
            onChange={e => this.setState({ repositoryInput: e.target.value })}
          />
          <button type="submit">
            {this.state.loading ? (
              <i className="fa fa-spinner fa-pulse" />
            ) : (
              "OK"
            )}
          </button>
        </Form>

        <CompareList
          repositories={this.state.repositories}
          onRefreshRepo={this.handleRefreshRepo}
          onDeleteRepo={this.handleDeleteRepo}
        />
      </Container>
    );
  }
}

export default Main;
